const { SideNavigation } = require("./side-navigation/side-navigation");

function initAll() {
  const $SideNavigationButtonSelector = "[data-module='dwp-side-navigation']";
  if (document.querySelector($SideNavigationButtonSelector)) {
    new SideNavigation($SideNavigationButtonSelector).init();
  }
}

module.exports = { initAll, SideNavigation };
